import React from "react";
import { Container, Row, Col } from "../components/Grid";
import "./style.css";
import { Card } from "react-bootstrap";
import { Input } from "../components/Form";
import Jumbotron from "../components/Jumbotron";
// import { Chart } from "chart.js";

function MoodTracker (){

  let todaysMood = [];
  let myChart;

  fetch("/api/todaysMood")
  .then(response => {
    return response.json();
  })
  .then(data => {
    // save db data on global variable
    todaysMood = data;

    populateTotal();
    populateTable();
    populateChart();
  });

  function populateTotal() {
    // reduce todaysMood amounts to a single total value
    let total = todaysMood.reduce((total, t) => {
      return total + parseInt(t.value);
    }, 0);

    let totalEl = document.querySelector("#total");
    totalEl.textContent = total;
  }



  return (
    <Container fluid>
    <form>
       <Card>
         <Card.Body>
         <Jumbotron>
              <h1>TRACK YOUR MOOD</h1>
            </Jumbotron>

            <Row className="form-group">
            <Col size="md-12">
            <h3> How do you feel today?</h3>

            <button className="gmoodBtton btn-success"> 
                    GOOD
            </button>
            <button className="ymoodBtton btn-success"> 
                    AVERAGE
            </button>
            <button className="rmoodBtton btn-success"> 
                    BAD
            </button>
                </Col>
                </Row>

          <Row className="form-group">
            <Col size="md-12">
            <h3>What Did You Do Today??</h3>
                  <Input 
                  className="todaysActivities"
                  type="todaysActivities" 
                  name="todaysActivities" 
                  id="todaysActivities" 
                  placeholder="Today I ...?" 
                  />
                </Col>
                </Row>

          <Row className="form-group">
            <Col size="md-12">
            <h3>What did you do today?</h3>
                  <Input 
                  className="doToday"
                  type="doToday" 
                  name="doToday" 
                  id="doToday" 
                  placeholder="Today i did.." 
                  />
                </Col>
                </Row>
          </Card.Body>
          <canvas ref={this.chartRef} />;
          <Card.Body>


          </Card.Body>
          </Card>
          </form>
          </Container>

    );
  }

  export default MoodTracker;
