// import React from "react";
// import Container from "../components/Container";
// import Row from "../components/Row";
// import Col from "../components/Col";

const { Collection } = require("mongoose");

const journalTitle = (".note-title");
const journalText = (".note-textarea");
const saveJournalBtn = (".save-note");
const newJournalBtn = (".new-note");
const journalList = (".list-container .list-group");

// activeNote is used to keep track of the note in the textarea
const activeJournal= {};

// A function for getting all notes from the db
const getJournal = function() {
  return ajax({
    url: "/api/notes",
    method: "GET"
  });
};

// A function for saving a note to the db
const saveJournal = function(note) {
  return ajax({
    url: "/api/notes",
    data: note,
    method: "POST"
  });
};

// A function for deleting a note from the db
const deleteJournal= function(id) {
  return ajax({
    url: "api/notes/" + id,
    method: "DELETE"
  });
};

// If there is an activeJournal, display it, otherwise render empty inputs
const renderActiveJournal = function() {
  saveJournalBtn.hide();

  if (activeJournal.id) {
    journalTitle.attr("readonly", true);
    journalText.attr("readonly", true);
    journalTitle.val(activeJournal.title);
    journalText.val(activeJournal.text);
  } else {
    journalTitle.attr("readonly", false);
    journalText.attr("readonly", false);
    journalTitle.val("");
    journalText.val("");
  }
};

// Get the journal data from the inputs, save it to the db and update the view
const handleJournalSave = function() {
  const newJournal = {
    title: journalTitle.val(),
    text: journalText.val()
  };

  saveJournal(newJournal).then(function(data) {
    getAndRenderJournal();
    renderActiveJournal();
  });
};

// Delete the clicked journal
const handleJournalDelete = function(event) {
  // prevents the click listener for the list from being called when the button inside of it is clicked
  event.stopPropagation();

  const journal = (this)
    .parent(".list-group-item")
    .data();

  if (activeJournal.id === journal.id) {
    activeJournal = {};
  }

  deleteJournal(journal.id).then(function() {
    getAndRenderJournal();
    renderActiveJournal();
  });
};

// Sets the activeJournaland displays it
const handleJournalView = function() {
  activeJournal = (this).data();
  renderActiveJournal();
};

// Sets the activeJournal to and empty object and allows the user to enter a new note
const handleNewJournalView = function() {
  activeJournal = {};
  renderActiveJournal();
};

// If a journal's title or text are empty, hide the save button
// Or else show it
const handleRenderSaveBtn = function() {
  if (journalTitle.val().trim() || journalText.val().trim()) {
    saveJournalBtn.hide();
  } else {
    saveJournalBtn.show();
  }
};

// Render's the list of note titles
const renderJournalList = function(journals) {
  journalList.empty();

  const journalListItems = [];

  for (const i = 0; i < journals.length; i++) {
    const journal = journals[i];

    const li = ("<li class='list-group-item'>").data(journal);
    const span = ("<span>").text(journal.title);
    const delBtn = (
      "<i class='fas fa-trash-alt float-right text-danger delete-note'>"
    );

    li.append(span, delBtn);
    journalListItems.push(li);
  }

  journalList.append(journalListItems);
};

// Gets journals from the db and renders them to the sidebar
const getAndRenderJournal = function() {
  return getJournal().then(function(data) {
    renderJournalList(data);
  });
};

savejournalBtn.on("click", handleJournalSave);
journalList.on("click", ".list-group-item", handleNewJournalView);
newJournalBtn.on("click", handleNewJournalView);
journalList.on("click", ".delete-journal", handleJournalDelete);
journalTitle.on("keyup", handleRenderSaveBtn);
journalText.on("keyup", handleRenderSaveBtn);

// Gets and renders the initial list of journals
getAndRenderJournal();


function Journal() {
    return (
      <div>
          <h1>Journal</h1>

        <Container style={{ marginTop: 30 }}>
        <Col size="md-4">
        <List>
        {journals.map(journal => (
        <ListItem key={journal._id}>
        <Link to={"/journals/" + journal._id}>
            </Link>
            <DeleteBtn onClick={() => deleteJournal(journal._id)} />
              </ListItem>
        ))}
        </List>    
        </Col>

        <Col size="md-8">
          <input
          className="journalsTextArea"
          type="textArea"
          />
        </Col>

       </Container>
      </div>
    );
  }

  export default Journal;
